ALTER PROCEDURE [valeant].[updateattachments]
	@advance bigint,
	@attachment [valeant].[attachmenttype] READONLY
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @createdTransaction bit
    IF @@TRANCOUNT = 0
    BEGIN
      SET @createdTransaction = 1
      SET XACT_ABORT ON
      BEGIN TRANSACTION
