ALTER PROCEDURE [valeant].[GetNextNumber] 
	@type bigint,
	@number bigint OUT
AS
BEGIN
	SET NOCOUNT ON;

    IF(NOT EXISTS(SELECT 'A' FROM [valeant].[numbers] WITH (UPDLOCK, ROWLOCK) WHERE [type] = @type))
	BEGIN
		SET @number = 1
